    async public void GetBalance()
    {
        string chain = "Binance";
        string network = "Mainnet";
        string contract = "0x924eBF416E8d207539eE36B1223b990db2Ae379E";
        string account = PlayerPrefs.GetString("Account");
        //string account = "0x0FE9f7fDf7cA835B556fd70823122808927670Ff";

        BigInteger temp = 1000000000;
        BigInteger balanceOf = await ERC20.BalanceOf(chain, network, contract, account);
        GameManager.instance.tokenBalance = balanceOf / temp;
        print(GameManager.instance.tokenBalance);
        GameManager.instance.DisplayDepositSection();
    }


